mapper.insert:
INSERT INTO BOARD_SHARING
(
BOARD_ID,
SHARING_COUNT
) VALUES
(
#{boardId},
#{sharingCount}
)

mapper.select:
SELECT TAG_ID, TAG_CONTENT
FROM TAG
WHERE TAG_CONTENT= #{tagContent}

SELECT * FROM ( SELECT BC.BOARD_ID, BC.USER_NUM, BC.PRODUCT_NAME, BC.PRODUCT_COMMENT, BC.TRANSACTION_ADDRESS, BC.TRANSACTION_CATEGORY, BC.CREATE_DATE AS, BC.VIEWS, BC.TRANSACTION_STATUS, BC.PRODUCT_CATEGORY_L, BC.PRODUCT_CATEGORY_M, BC.PRODUCT_CATEGORY_S, BR.RENTAL_FEE, BR.DEPOSIT, BR.RENTAL_START_DATE, BR.RENTAL_END_DATE, C.CATEGORY_PATH, F.FILE_NAME, ROW_NUMBER() OVER (PARTITION BY BC.BOARD_ID ORDER BY F.FILE_NAME) AS RN FROM BOARD_COMMON BC JOIN BOARD_RENTAL BR ON BC.BOARD_ID = BR.BOARD_ID JOIN "FILE" F ON F.REF_NO = BC.BOARD_ID JOIN CATEGORY C ON F.CATEGORY_ID = C.CATEGORY_ID WHERE F.CATEGORY_ID = 6 ) WHERE RN = 1
AND RENTAL_FEE >= #{minRentalFee}
AND RENTAL_FEE <= #{maxRentalFee}
AND RENTAL_START_DATE >= #{startDate}
AND RENTAL_END_DATE <= #{endDate}
AND PRODUCT_CATEGORY_L = #{productCategoryL}
AND PRODUCT_CATEGORY_M = #{productCategoryM}
AND PRODUCT_CATEGORY_S = #{productCategoryS}
ORDER BY VIEWS DESC
ORDER BY RENTAL_FEE DESC
ORDER BY CREATE_DATE DESC

mapper.update:
UPDATE BOARD_COMMON SET
VIEWS = VIEWS +1
WHERE BOARD_ID = #{boardId}

mapper.delete:
DELETE FROM DIBS
WHERE LIKES_USER_ID = #{likesUserId} AND BOARD_ID = #{boardId} AND
BOARD_CATEGORY = #{boardCategory}

mapper.select:
SELECT COUNT(*)
FROM DIBS
WHERE BOARD_ID = #{boardId} AND BOARD_CATEGORY = #{boardCategory}

SELECT BOARD_ID
FROM DIBS
WHERE LIKES_USER_ID = #{userNum}

SELECT ADDRESS
FROM USER_TB
WHERE USER_NUM = #{userNum}

SELECT * FROM ( SELECT BC.BOARD_ID, BC.USER_NUM, BC.PRODUCT_NAME, BC.PRODUCT_COMMENT, BC.TRANSACTION_ADDRESS, BC.TRANSACTION_CATEGORY, BC.CREATE_DATE AS, BC.VIEWS, BC.TRANSACTION_STATUS, BC.PRODUCT_CATEGORY_L, BC.PRODUCT_CATEGORY_M, BC.PRODUCT_CATEGORY_S, BS.SHARING_COUNT, C.CATEGORY_PATH, F.FILE_NAME, ROW_NUMBER() OVER (PARTITION BY BC.BOARD_ID ORDER BY F.FILE_NAME) AS RN FROM BOARD_COMMON BC JOIN BOARD_SHARING BS ON BC.BOARD_ID = BS.BOARD_ID JOIN "FILE" F ON F.REF_NO = BC.BOARD_ID JOIN CATEGORY C ON F.CATEGORY_ID = C.CATEGORY_ID WHERE F.CATEGORY_ID = 7 ) WHERE RN = 1
AND PRODUCT_CATEGORY_L = #{productCategoryL}
AND PRODUCT_CATEGORY_M = #{productCategoryM}
AND PRODUCT_CATEGORY_S = #{productCategoryS}
ORDER BY VIEWS DESC
ORDER BY CREATE_DATE DESC

SELECT * FROM ( SELECT BC.BOARD_ID, BC.USER_NUM, BC.PRODUCT_NAME, BC.PRODUCT_COMMENT, BC.TRANSACTION_ADDRESS, BC.TRANSACTION_CATEGORY, BC.CREATE_DATE AS, BC.VIEWS, BC.TRANSACTION_STATUS, BC.PRODUCT_CATEGORY_L, BC.PRODUCT_CATEGORY_M, BC.PRODUCT_CATEGORY_S, BA.AUCTION_STARTING_FEE, BA.BID_UNIT, BA.AUCTION_START_DATE, BA.AUCTION_END_DATE, C.CATEGORY_PATH, F.FILE_NAME, ROW_NUMBER() OVER (PARTITION BY BC.BOARD_ID ORDER BY F.FILE_NAME) AS RN FROM BOARD_COMMON BC JOIN BOARD_AUCTION BA ON BC.BOARD_ID = BA.BOARD_ID JOIN "FILE" F ON F.REF_NO = BC.BOARD_ID JOIN CATEGORY C ON F.CATEGORY_ID = C.CATEGORY_ID WHERE F.CATEGORY_ID = 8 ) WHERE RN = 1
AND AUCTION_START_DATE >= #{startDate}
AND AUCTION_END_DATE <= #{endDate}
AND PRODUCT_CATEGORY_L = #{productCategoryL}
AND PRODUCT_CATEGORY_M = #{productCategoryM}
AND PRODUCT_CATEGORY_S = #{productCategoryS}
ORDER BY VIEWS DESC
ORDER BY CREATE_DATE DESC
